/* this needs to be declared after profanity_regex has been populated */
preferences.watch("profanity-filter", (_filters) => {
    filters = {};
    for (const lang in _filters) {
        if (_filters[lang] && lang in profanity_regex) {
            filters[lang] = true;
        }
    }
});
